import React, { Component } from "react";
import Navbar from "../components/Navbar";
import * as Yup from "yup";
import { FormInput, FormDevider } from "../components/FormInput";
import { useForm } from "react-hook-form";
import { Button, OutlineButton } from "../components/Buttons";
import GoogleIcon from "../assets/icons/1004px-Google__G__Logo.svg.png";
import TwitterIcon from "../assets/icons/580b57fcd9996e24bc43c53e.png";
import { useParams } from "react-router-dom";

export class SignUp extends Component {
    static propTypes = {};

    render() {
      return (
        <div className="background">
          <Navbar />
          <SignUpBlock />
        </div>
      );
    }
}
    export default SignUp;

    function SignUpBlock() {
        return (
          <div className="signInBlock">
            <h2> Create Free Account </h2>

            {/* <p> Already have an account?
            <a href="#"> sign in </a> </p> */}


            <div className="haveAccount">
                <p> Already have an account? 
              <a href="#"> sign in </a> </p>

              <SignUpForm />
            </div>
          </div>
        );
      }

      export const SignUpForm = () => {
        //form validation
        const { register, handleSubmit, errors } = useForm({
          mode: "onSubmit",
          validationSchema: Yup.object({
            FirstName: Yup.string()
            .name("Please Enter A valid first name")
            .required("Required"),
            LastName: Yup.string()
            .lname("Please Enter A valid last name")
            .required("Required"),
            email: Yup.string()
            .email("Please Enter A Valid Email")
            .required("Required"),
            password: Yup.string()
            .min(6, "Password should be longer than 6 characters")
            .required("Required"),
            verifypassword: Yup.string()
            .min(6, "Password should be longer than 6 characters")
            .required("Required"),
          }),
        });

        //here we do the request once the validation is success 👍🏾
        const onSubmit = ({ FirstName,LastName,email, password, verifypassword }) => {
          alert(` Login: ${email}, password: ${password}`);
        };
        return (
          <form onSubmit={handleSubmit(onSubmit)}>
            <FormInput
              id="email"
              name="email"
              type="email"
              placeHolder="email"
              register={register}
              errors={errors.email}
            />
            <FormInput
              id="password"
              name="password"
              type="password"
              placeHolder="password"
              register={register}
              errors={errors.password}
            />
            <span className="remember-forgetpass">
              <span className="rememberMe">
                <input type="checkbox" />
                <span>remember me?</span>
              </span>
              <a href='#'>Forget Password</a>
            </span>
            <Button
              type="submit"
              title="Login"
              onClick={() => console.log("Pressed")}
            />
            <FormDevider />

            <OutlineButton
              color={"#4285f4"}
              image={GoogleIcon}
              onClick={() => console.log("Google SignIn")}
              title="Continue With Google"
            />
            <OutlineButton
              color={"#00A2F5"}
              image={TwitterIcon}
              onClick={() => console.log("Twitter SignIn")}
              title="Continue With Twitter"
            />
          </form>
        );
      };
